import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

class ProductList extends React.Component {
  renderProducts() {
    const { activeId, products } = this.props;
    products.sort(
      (a, b) => new Date(b.available_date) - new Date(a.available_date),
    );

    return products.map(product => (
        <li key={product.id}>
          <Link to={`/products/${product.id}`} className={activeId === product.id ? 'active' : ''}>
            {product.available_date}
            {' - '}
            {product.product_name}
          </Link>
        </li>
      ));
  }

  render() {
    return (
      <section className="productList">
        <h2>
          Products
          <Link to="/products/new">New Product</Link>
        </h2>
        <ul>{this.renderProducts()}</ul>
      </section>
    );
  }
}

ProductList.propTypes = {
    activeId: PropTypes.number,
    products: PropTypes.arrayOf(PropTypes.object),
};

ProductList.defaultProps = {
    activeId: undefined,
    products: [],
};

export default ProductList;
