import React from 'react';
import PropTypes from 'prop-types';

class ProductForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
        product: props.product,
        errors: {},
      };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    const { product } = this.state;
    const errors = this.validateProduct(product);
    if (!this.isEmptyObject(errors)) {
      this.setState({ errors });
    } else {
      console.log(product);
    }
  }

  validateProduct(product) {
    const errors = {};

    if (product.product_name === '') {
      errors.product_name = 'You must enter a product name';
    }

    if (product.available_date === '') {
      errors.available_date = 'You must enter a valid date';
    }

    if (product.product_upc === '') {
      errors.title = 'You must enter a upc';
    }

    if (product.property_name === '') {
      errors.property_name = 'You must enter a property name';
    }

    if (product.property_value === '') {
      errors.property_value = 'You must enter a property value';
    }

    console.log(product);
    return errors;
  }

  isEmptyObject(obj) {
    return Object.keys(obj).length === 0;
  }

  handleInputChange(product) {
    const { target } = product;
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState(prevState => ({
      product: {
        ...prevState.product,
        [name]: value,
      },
    }));
  }

  render() {
    return (
      <div>
        <h2>New Product</h2>
        <form className="eventForm" onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor="product_name">
              <strong>Name:</strong>
              <input type="text" id="product_name" name="product_name" />
            </label>
          </div>
          <div>
            <label htmlFor="available_date">
              <strong>Date:</strong>
              <input type="text" id="available_date" name="available_date" />
            </label>
          </div>
          <div>
            <label htmlFor="product_upc">
              <strong>UPC:</strong>
              <textarea cols="30" rows="10" id="product_upc" name="product_upc" />
            </label>
          </div>
          <div>
            <label htmlFor="property_name">
              <strong>Property Name:</strong>
              <input type="text" id="property_name" name="property_name" />
            </label>
          </div>
          <div>
            <label htmlFor="property_value">
              <strong>Property Value:</strong>
              <input type="text" id="property_value" name="property_value" />
            </label>
          </div>
          <div className="form-actions">
            <button type="submit">Save</button>
          </div>
        </form>
      </div>
    );
  }
}

export default ProductForm;
